import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import { addToCart } from "../../redux/actions";
import { useDispatch } from "react-redux";

const useStyles = makeStyles({
	card: {
		color: colors.secondary,
		display: "flex",
		// flexDirection: "column",
		alignItems: "center",
		marginTop: 40,
		"& img": {
			width: 200,
		},
	},
	cardDescribe: {
		height: "100%",
		display: "flex",
		flexDirection: "column",
		alignItems: "space-around",
	},
	cardInfo: {
		backgroundColor: colors.primary,
		display: "flex",
		flexGrow: 1,
		height: "100%",
		alignItems: "center",
		"& h5": {
			fontSize: 22,
			fontWeight: 600,
			lineHeight: "30px",
		},
	},
	cardBuy: {
		marginTop: 10,
		display: "flex",
		justifyContent: "space-around",
		"& h5": {
			fontSize: 45,
			fontWeight: 400,
			padding: "0 20px 0 40px",
		},
	},
	noAvailable: {
		top: 20,
		color: colors.secondary,
		fontSize: 20,
		fontFamily: "Cousine",
	},
});

export default function CardInCart({ itemCard, styles }) {
	const classes = useStyles();
	const dispatch = useDispatch();

	return (
		<div style={styles} className={classes.card}>
			<img src={itemCard.img} alt="item" />
			<div className={classes.cardInfo}>
				<div className={classes.cardDescribe}>
					<h5>
						{itemCard.type}
						<br />
						{itemCard.name}
					</h5>
					<div>
						{!itemCard.available && (
							<span className={classes.noAvailable}>Немає в наявності</span>
						)}
					</div>
				</div>
				<div className={classes.cardBuy}>
					<h5>{itemCard.price} грн</h5>
				</div>
			</div>
		</div>
	);
}
