import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import { colors } from "../../theme/colors";
import { useSelector } from "react-redux";
import CatalogSelect from "./components/CatalogSelect";

const useStyles = makeStyles((menuWidth) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    position: "relative",
    flexGrow: 1,
    maxWidth: (menuWidth) => menuWidth,
    transition: ".2s",
  },
  hide: {
    display: "none",
  },
  drawer: {
    width: (menuWidth) => menuWidth,
    transition: ".2s",
  },
  drawerPaper: {
    padding: 15,
    width: (menuWidth) => menuWidth,
    transition: ".2s",
    borderRight: `1px solid ${colors.secondary}`,
    position: "absolute",
  },
}));

export default function Catalog() {
  const menuWidth = useSelector((state) => state.app.menuWidth);
  const currentPath = useSelector((state) => state.app.location);
  const classes = useStyles(menuWidth);

  const menuItems = ["Табак", "Витратники", "Сигари", "Аксесуари"];

  return currentPath === "/order" ? null : (
    <div className={classes.root}>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={Boolean(menuWidth)}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <List>
          {menuItems.map((text, index) => (
            <ListItem button key={text}>
              <ListItemText primary={text} />
            </ListItem>
          ))}
          <CatalogSelect filterName="Brand" />
        </List>
      </Drawer>
    </div>
  );
}
