import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import clsx from "clsx";

const useStyles = makeStyles({
	markItems: {
		display: "flex",
		marginLeft: -15,
	},
	mark: {
		marginLeft: 15,
		width: 35,
		height: 35,
		border: `1px solid ${colors.secondary}`,
		borderRadius: "50%",
	},
	activeMark: {
		backgroundColor: "yellow",
		border: "none",
	},
});

export default function CustomMarks() {
	const classes = useStyles();

	let marks = [1, 2, 3, 4, 5];
	const [activeMarks, setActiveMarks] = useState(0);

	const changeActiveMarks = (num) => {
		setActiveMarks(num);
		console.log(num);
	};

	return (
		<div className={classes.markItems}>
			{marks.map((marksItem) => (
				<div
					key={marksItem}
					id={marksItem}
					className={clsx(
						classes.mark,
						marksItem <= activeMarks && classes.activeMark
					)}
					onClick={(e) => changeActiveMarks(e.target.id)}
				></div>
			))}
		</div>
	);
}
