import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import { addToCart } from "../../redux/actions";
import { useDispatch } from "react-redux";

const useStyles = makeStyles({
	card: {
		width: 300,
		color: colors.primary,
		display: "flex",
		flexDirection: "column",
		alignItems: "center",
		marginTop: 50,
		"& img": {
			width: "100%",
			border: `1px solid ${colors.secondary}`,
		},
		"&:hover": {
			boxShadow: "inset 0px 0px 10px rgba(0,0,0,0.6)",
			transition: "box-shadow .2s",
			cursor: "default",
		},
	},
	cardDescribe: {
		"& h5": {
			marginTop: 5,
		},
	},
	cardInfo: {
		width: "100%",
		backgroundColor: colors.secondary,
		padding: "5px 15px",
		"& p": {
			fontSize: 16,
			fontWeight: 300,
		},
		"& h5": {
			fontSize: 18,
			fontWeight: 600,
		},
	},
	cardBuy: {
		marginTop: 10,
		display: "flex",
		justifyContent: "space-around",
		"& h5": {
			fontSize: 25,
		},
	},
	noAvailable: {
		position: "absolute",
		top: 20,
		color: colors.secondary,
		fontSize: 20,
		fontFamily: "Cousine",
	},
});

export default function CardInCart({ itemCard, styles }) {
	const classes = useStyles();
	const dispatch = useDispatch();

	return (
		<div style={styles} className={classes.card}>
			{!itemCard.available && (
				<span className={classes.noAvailable}>Немає в наявності</span>
			)}
			<img src={itemCard.img} alt="item" />
			<div className={classes.cardInfo}>
				<div className={classes.cardDescribe}>
					<p>{itemCard.type}</p>
					<h5>{itemCard.name}</h5>
				</div>
				<div className={classes.cardBuy}>
					<h5>{itemCard.price} грн</h5>
				</div>
			</div>
		</div>
	);
}
