import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useDispatch, useSelector } from "react-redux";
import CardInCart from "../components/itemCards/CardInCart";
import { colors } from "../theme/colors";
import CustomSelect from "../components/menuItems/CustomSelect";
import { useForm } from "react-hook-form";

const useStyles = makeStyles({
	root: {
		"& h1": {
			padding: 40,
			paddingTop: 20,
			width: "100%",
			fontFamily: "Open Sans Condensed",
			textAlign: "center",
			fontSize: 54,
		},
	},
	form: {
		display: "flex",
	},
	mainOrder: {
		width: "fit-content",
		border: `1px solid ${colors.secondary}`,
		margin: 20,
		"& h2": {
			fontSize: 35,
			fontWeight: 600,
			padding: 30,
			textAlign: "center",
		},
	},
	cartItems: {
		margin: "0 20px 20px 20px",
		maxHeight: 620,
		overflow: "scroll",
		wodth: "fit-content",
		"&::-webkit-scrollbar": {
			width: 5,
			height: 0,
			borderRadius: "10px",
		},
		"&::-webkit-scrollbar-thumb": {
			background: colors.blackGrey,
			borderRadius: "10px",
		},
	},
	notFound: {
		fontSize: 30,
		textAlign: "center",
		margin: "30px 0",
		width: "100%",
	},
	mainForm: {
		width: "35%",
		marginLeft: "5%",
		"& h3": {
			fontSize: 34,
			fontWeight: 600,
			padding: "40px 0",
		},
	},
});

export default function Order() {
	const classes = useStyles();

	const cart = useSelector((state) => state.cart.cart);
	const paymenntChilds = ["Карта приватбанк", "Наложений платіж"];
	const deliveryChilds = ["Нова пошта", "Самовивіз"];

	const { handleSubmit, register } = useForm();

	const onSubmit = async (data, e) => {
		let formdata = new FormData();
		formdata.append("payment", data.login);
		formdata.append("delivery", data.password);
		console.log(data);

		// let info = await dispatch(authUser(formdata))
		// if (info) {
		// 	if (info.status === "error") {
		// 		setResponseInfo(info.message)
		// 		await sleep(5000)
		// 		setResponseInfo("")
		// 	} else {
		// 		localStorage.setItem('token', info.message.token)
		// 		redirect("")
		// 	}
		// }
	};

	return (
		<div className={classes.root}>
			<h1>Оформлення замовлення</h1>
			<form className={classes.form} onSubmit={handleSubmit(onSubmit)}>
				<div className={classes.mainForm}>
					<div>
						<h3>Оплата</h3>
						<CustomSelect
							text="Оберіть спосіб оплати"
							childs={paymenntChilds}
							formName="payment"
						/>
					</div>
					<div>
						<h3>Доставки</h3>
						<CustomSelect
							text="Оберіть спосіб доставки"
							childs={deliveryChilds}
							formName="delivery"
						/>
					</div>
				</div>

				<div className={classes.mainOrder}>
					<h2>Ваше замовлення</h2>
					<div className={classes.cartItems}>
						{cart[0] ? (
							cart.map((cartItem, index) => {
								return (
									<CardInCart itemCard={cartItem} key={index} index={index} />
								);
							})
						) : (
							<div className={classes.notFound}>Виберіть товар</div>
						)}
					</div>
				</div>

				<button
					className={classes.button}
					variant="contained"
					color="primary"
					type="submit"
				>
					Submit
				</button>
			</form>
		</div>
	);
}
