import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import CustomButton from "../menuItems/CustomButton";
import { addToCart } from "../../redux/actions";
import { useDispatch, useSelector } from "react-redux";
import { NavLink } from "react-router-dom";

const useStyles = makeStyles({
	cardLink: {
		position: "relaive",
		zIndex: 1,
	},
	card: {
		position: "relative",
		width: 300,
		color: colors.primary,
		display: "flex",
		flexDirection: "column",
		alignItems: "center",
		marginLeft: 50,
		"& img": {
			width: "100%",
			border: `1px solid ${colors.secondary}`,
		},
		"&:hover": {
			boxShadow: "inset 0px 0px 10px rgba(0,0,0,0.6)",
			transition: "box-shadow .2s",
			cursor: "default",
		},
	},
	cardDescribe: {
		"& h5": {
			marginTop: 5,
		},
	},
	cardInfo: {
		width: "100%",
		backgroundColor: colors.secondary,
		padding: "5px 15px",
		"& p": {
			fontSize: 16,
			fontWeight: 300,
		},
		"& h5": {
			fontSize: 18,
			fontWeight: 600,
		},
	},
	cardBuy: {
		marginTop: 10,
		display: "flex",
		justifyContent: "space-around",
		"& h5": {
			fontSize: 25,
		},
	},
	noAvailable: {
		position: "absolute",
		top: 20,
		color: colors.secondary,
		fontSize: 20,
		fontFamily: "Cousine",
	},
});

export default function MainCard({ itemCard, styles }) {
	const classes = useStyles();
	const dispatch = useDispatch();

	const cart = useSelector((state) => state.cart.cart);

	let buttonStyles = {
		position: "relaive",
		fontWeight: 600,
		fontSize: 20,
		minWidth: 80,
		borderRadius: 3,
		backgroundColor: colors.semiWhite,
		padding: "5px 12px",
		marginRight: 15,
		zIndex: 10,
	};

	return (
		<NavLink className={classes.cardLink} to={"/article/" + itemCard.id}>
			<div style={styles} className={classes.card}>
				{!itemCard.available && (
					<span className={classes.noAvailable}>Немає в наявності</span>
				)}
				<img src={itemCard.img} alt="item" />
				<div className={classes.cardInfo}>
					<div className={classes.cardDescribe}>
						<p>{itemCard.type}</p>
						<h5>{itemCard.name}</h5>
					</div>
					<div className={classes.cardBuy}>
						<h5>{itemCard.price} грн</h5>
						<CustomButton
							primary={colors.primary}
							secondary={colors.secondary}
							text="КУПИТИ"
							action={() => dispatch(addToCart(itemCard, cart))}
							styles={buttonStyles}
							enabled={false}
						/>
					</div>
				</div>
			</div>
		</NavLink>
	);
}
