import { FETCH_TOBACCO, MENU_WIDTH } from "./types";

export const fetchTobacco = async (tobacco) => {
	return (dispatch) => {
		dispatch({ type: FETCH_TOBACCO, payload: tobacco });
	};
};

export const changeStateMenu = async (menuWidth) => {
	return (dispatch) => {
		dispatch({ type: MENU_WIDTH, payload: stateMenu });
	};
};
