import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Catalog from "../menu/catalog";
import CustomInput from "../menuItems/CustomInput";
import Nav from "./components/Nav";
import Cart from "../cart/Cart";
import searchIcon from "../../theme/images/search.svg";
import { colors } from "../../theme/colors";
import CustomButton from "../menuItems/CustomButton";

const headerHeight = 60;

const useStyles = makeStyles({
	header: {
		display: "flex",
		justifyContent: "center",
		width: "100%",
		borderTop: `1px solid ${colors.secondary}`,
		borderBottom: `1px solid ${colors.secondary}`,
		backgroundColor: "black",
	},
	container: {
		display: "flex",
		width: "100%",
		maxWidth: 2000,
	},
	search: {
		width: "100%",
		maxWidth: 600,
		padding: "0 10px 0 25px",
		display: "flex",
		alignItems: "center",
		borderRight: `1px solid ${colors.secondary}`,
		"& img": {
			cursor: "pointer",
			height: 35,
		},
	},
	cart: {
		padding: "5px 10px 5px 20px",
		borderTop: `1px solid ${colors.secondary}`,
		borderBottom: `1px solid ${colors.secondary}`,
		width: "50%",
	},
});

export default function Header({ handleDrawer }) {
	const classes = useStyles();

	let buttonStyles = {
		fontWeight: 700,
		fontSize: 25,
		minWidth: 220,
		height: headerHeight,
		fontFamily: "Open Sans Condensed",
	};

	return (
		<header className={classes.header}>
			<div className={classes.container}>
				<CustomButton
					primary={colors.secondary}
					secondary={colors.primary}
					text="Каталог"
					action={handleDrawer}
					styles={buttonStyles}
					enabled={false}
				/>
				<Nav headerHeight={headerHeight} />
				<span className={classes.search}>
					<CustomInput rubber={true} text="Пошук товарів" />
					<img src={searchIcon} alt="search" />
				</span>
				<div className={classes.cart}>
					<Cart />
				</div>
			</div>
		</header>
	);
}
