import {
	PUSH_ITEM_CART,
	DEL_ITEM_CART,
	COUNT_TOTAL,
	CHANGE_AMOUNT,
} from "../types";

const initialState = {
	cart: null,
	total: 0,
};

export const cartReducer = (state = initialState, action) => {
	switch (action.type) {
		case PUSH_ITEM_CART:
			return {
				...state,
				cart: state.cart ? [...state.cart, action.payload] : [action.payload],
			};

		case DEL_ITEM_CART:
			return {
				...state,
				cart: state.cart.filter(
					(cartItem) => cartItem.type !== action.payload.type
				),
			};

		case COUNT_TOTAL:
			return {
				...state,
				total: state.total + action.payload.price * action.payload.amount,
			};

		case CHANGE_AMOUNT:
			return {
				...state,
				cart: state.cart.map((cartItem) => {
					cartItem.id === action.payload.id &&
						(cartItem.amount = action.payload.amount);
				}),
			};

		default:
			return state;
	}
};
