import {
	PUSH_ITEM_CART,
	DEL_ITEM_CART,
	COUNT_TOTAL,
	CHANGE_AMOUNT,
} from "../types";

const initialState = {
	cart: null,
	total: 0,
};

export const cartReducer = (state = initialState, action) => {
	switch (action.type) {
		case PUSH_ITEM_CART:
			return {
				...state,
				cart: state.cart ? [...state.cart, action.payload] : [action.payload],
			};

		case DEL_ITEM_CART:
			return {
				...state,
				cart: state.cart.filter(
					(cartItem) => cartItem.type !== action.payload.type
				),
			};

		// FIX
		case COUNT_TOTAL:
			return {
				...state,
				total: state.cart.reduce((counter, singleCartItem) => {
					counter + singleCartItem;
				}),
			};

		case CHANGE_AMOUNT:
			return { ...state, cart: action.payload };

		default:
			return state;
	}
};
