import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import MainCard from "./MainCard";
import { useSelector } from "react-redux";

const cardSize = 300;
const cardMargin = 50;
const cardLength = cardSize + cardMargin;
const menuSize = 220;
const maxWidth = 2000;

const useStyles = makeStyles((carouselWidth) => ({
	carousel: {
		width: (carouselWidth) => carouselWidth,
		flexGrow: 1,
		overflow: "hidden",
		position: "relative",
		height: "100%",
	},
	carouselInner: {
		display: "flex",
		width: "fit-content",
		position: "absolute",
	},
}));

export default function Carousel() {
