	// 		);
	// 		document.querySelector("#carousel").style.width = `${carouselWidth}+px`;
	// 	}
	// };

	// const [windowWidth, setWindowWidth] = useState(2000);
	const [carouselWidth, setCarouselWidth] = useState(0);

	useEffect(() => {
		const carousel = document.querySelector("#carousel");
		const content = document.querySelector("#content");
	}, []);

	const setWidth = () => {
		const contentWidth = content.clientWidth;
		let counter = 0;

		while (true) {
			contentWidth - cardLength;
			if (contentWidth > 0) {
				counter++;
			} else {
				break;
			}
		}
		console.log(counter);

		carousel.style.width = `${cardLength * counter}+px`;
	};

	const classes = useStyles(carouselWidth);

	window.addEventListener("resize", () => setWidth());

	const tobaccos = useSelector((state) => state.tobacco.cards);

	return (
		<div id="carousel" className={classes.carousel}>
			<div className={classes.carouselInner}>
				{tobaccos.map((tobacco, index) => {
					return <MainCard key={index} tobacco={tobacco} />;
				})}
			</div>
		</div>
	);
}
