import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Carousel from "../components/itemCards/Carousel";
import Divider from "../components/Divider";
import ContactsBlock from "../components/footer/ContactsBlock";
import { useSelector } from "react-redux";

const useStyles = makeStyles({
	itemGroup: {
		margin: "60px 0",
		"& h1": {
			width: "fit-content",
			marginLeft: 50,
			fontFamily: "Open Sans Condensed",
			fontSize: 54,
			marginBottom: 30,
		},
	},
});

export default function Main() {
	const classes = useStyles();

	const tobaccos = useSelector((state) => state.tobacco.cards);

	const carousels = [
		{
			name: "Табак",
			id: "tobacco-carousel",
			type: tobaccos,
		},
		{
			name: "Витратники",
			id: "consumables-carousel",
			type: tobaccos,
		},
		{
			name: "Сигари",
			id: "cigarettes-carousel",
			type: tobaccos,
		},
		{
			name: "Аксесуари",
			id: "accessories-carousel",
			type: tobaccos,
		},
	];

	return (
		<div className={classes.main}>
			{carousels.map((carousel, index) => {
				return (
					<div key={index}>
						<div className={classes.itemGroup}>
							<h1>{carousel.name}</h1>
							<Carousel escalator={carousel} />
						</div>
						<Divider />
					</div>
				);
			})}
			<ContactsBlock />
		</div>
	);
}
