import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {NavLink} from 'react-router-dom'
import CustomButton from '../../menuItems/CustomButton'
import { colors } from '../../../theme/colors'

const useStyles = makeStyles({
	nav: {
		display: "flex",
	},
	active: {
		color: colors.secondary,
		backgroundColor: colors.primary,
	}
})

export default function Nav({headerHeight}) {
  const classes = useStyles();

  const [buttons, setButtons] = useState([
	  {
	  	name: "Головна",
	  	link: "/",
	  	active: true
	  },
	  {
	  	name: "Про Нас",
	  	link: "/about",
	  	active: false
	  },
	  {
	  	name: "Контакти",
	  	link: "/contacts",
	  	active: false
	  },
	  {
	  	name: "Відгуки",
	  	link: "/feedbacks",
	  	active: false
	  },
  ])

  let buttonStyles = {
    fontWeight: 700,
    fontSize: 25,
    height: headerHeight,
    width: 120,
    fontFamily: 'Open Sans Condensed',
  }

  function setActive (id) {
  	let butts = buttons
  	buttons.map((butt, index) => {
	  	butts[index].active = false
	  	butts[id].active = true
  	})
  	setButtons(butts)
  }

  return (
    <nav className={classes.nav}>
      	{buttons.map((button, index) => (
	      	<NavLink activeClassName={classes.active} key={index} to={button.link}>
	      		<CustomButton 
			        primary = {colors.primary}
			        secondary = {colors.secondary}
			        text = {button.name}
			        styles = {buttonStyles}
			        action = {() => setActive(index)}
			        open = {button.active}
			        enabled = {true}
			      />
		      </NavLink>
      	))}
    </nav>
  );
}
