import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import CustomButton from "../menuItems/CustomButton";
import cigarette1 from "../../theme/images/cigarette1.svg";

const useStyles = makeStyles({
	card: {
		maxWidth: 298,
		color: colors.primary,
	},
	cardInfo: {
		backgroundColor: colors.secondary,
		padding: "5px 15px",
		"& p": {
			fontSize: 16,
			fontWeight: 300,
		},
		"& h5": {
			fontSize: 18,
			fontWeight: 600,
		},
	},
	cardBuy: {
		marginTop: 10,
		display: "flex",
		alignItems: "center",
		justifyContent: "space-around",
		"& h5": {
			fontSize: 25,
		},
	},
});

export default function MainCard() {
	const classes = useStyles();

	let buttonStyles = {
		fontWeight: 600,
		fontSize: 20,
		minWidth: 80,
		borderRadius: 3,
		backgroundColor: colors.semiWhite,
		padding: "5px 12px",
		marginRight: 15,
	};

	return (
		<div className={classes.card}>
			<img src={cigarette1} alt="item" />
			<div className={classes.cardInfo}>
				<div className={classes.cardDescribe}>
					<p>Тютюн для самокруток</p>
					<h5>Mac Baren Cherry Choice 40 г.</h5>
				</div>
				<div className={classes.cardBuy}>
					<h5>180 грн</h5>
					<CustomButton
						primary={colors.primary}
						secondary={colors.secondary}
						text="КУПИТИ"
						action={(e) => console.log(e)}
						styles={buttonStyles}
						enabled={false}
					/>
				</div>
			</div>
		</div>
	);
}
