import { FETCH_TOBACCO } from "../types";
import cigarette1 from "../../theme/images/cigarette1.png";
import noCigarette from "../../theme/images/no-cigarette.png";

const initialState = {
	cards: [
		{
			img: cigarette1,
		},
	],
	total: 0,
	filters: null,
	currentPage: 1,
};

export const tobaccoReducer = (state = initialState, action) => {
	switch (action.type) {
		case FETCH_TOBACCO:
			return { ...state, cards: action.payload };

		default:
			return state;
	}
};
