import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useSelector } from "react-redux";
import MainCard from "../components/itemCards/MainCard";

const useStyles = makeStyles({
	root: {
		"& h1": {
			width: "fit-content",
			margin: 30,
			fontFamily: "Open Sans Condensed",
			fontSize: 54,
		},
	},
	main: {
		display: "flex",
		flexWrap: "wrap",
		marginBottom: 70,
		justifyContent: "center",
	},
});

export default function Feedbacks() {
	let splitedHref = window.location.href.split("/");
	let currentItems = splitedHref[splitedHref.length - 1];

	const classes = useStyles();
	let currentItemList = {};
	let currentName = "";

	if (currentItems === "tobacco") {
		currentItemList = useSelector((state) => state.tobacco.cards);
		currentName = "Табак";
	}

	let cardStyles = {
		marginTop: 70,
	};

	return (
		<div className={classes.root}>
			<h1>{currentName}</h1>
			<div className={classes.main}>
				{currentItemList.map((currentItem, index) => {
					return (
						<MainCard styles={cardStyles} key={index} itemCard={currentItem} />
					);
				})}
			</div>
		</div>
	);
}
