import {
	FETCH_CARDS,
	MENU_WIDTH,
	PUSH_ITEM_CART,
	DEL_ITEM_CART,
	COUNT_TOTAL,
	CHANGE_AMOUNT,
} from "./types";

// export const fetchItems = (itemsType) => {
// 	return async (dispatch) => {
// 		dispatch({ type: FETCH_CARDS, payload: {} });
// 	};
// };

export const changeWidthMenu = (menuWidth) => {
	return (dispatch) => {
		dispatch({ type: MENU_WIDTH, payload: menuWidth });
	};
};

export const addToCart = (itemCard, cart) => {
	return async (dispatch) => {
		if (cart) {
			const checkCart = cart.map((cartItem, index) => {
				if (cartItem.id === itemCard.id) {
					const newCart = cart;
					newCart[index].amount += 1;
					dispatch({ type: CHANGE_AMOUNT, payload: newCart });
					return false;
				}
				return true;
			});
			if (checkCart) {
				dispatch({ type: PUSH_ITEM_CART, payload: itemCard });
				dispatch({ type: COUNT_TOTAL, payload: itemCard.price });
			}
		} else {
			dispatch({ type: PUSH_ITEM_CART, payload: itemCard });
			dispatch({ type: COUNT_TOTAL, payload: itemCard.price });
		}
	};
};

export const changeAmount = (inputValue, cart, index) => {
	return async (dispatch) => {
		const newCart = cart;
		newCart[index].amount = inputValue;
		dispatch({ type: CHANGE_AMOUNT, payload: newCart });
	};
};
