import { FETCH_CARDS, MENU_WIDTH } from "./types";

export const fetchTobacco = (tobacco) => {
	return async (dispatch) => {
		dispatch({ type: FETCH_CARDS, payload: tobacco });
	};
};

export const changeWidthMenu = (menuWidth) => {
	return (dispatch) => {
		dispatch({ type: MENU_WIDTH, payload: menuWidth });
	};
};
