import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import CustomButton from "../menuItems/CustomButton";
import cigarette1 from "../../theme/images/cigarette1.svg";

const useStyles = makeStyles({
	card: {
		maxWidth: 298,
		color: colors.primary,
	},
	cardInfo: {
		backgroundColor: colors.secondary,
		padding: "5px 15px",
		"& p": {
			fontSize: 16,
		},
		"& h5": {
			fontSize: 18,
		},
	},
});

export default function MainCard() {
	const classes = useStyles();

	let buttonStyles = {
		// fontWeight: 700,
		// fontSize: 25,
		// minWidth: 220,
		// height: headerHeight,
		// fontFamily: "Open Sans Condensed",
	};

	return (
		<div className={classes.card}>
			<img src={cigarette1} alt="item" />
			<div className={classes.cardInfo}>
				<div className={classes.cardDescribe}>
					<p>Тютюн для самокруток</p>
					<h5>Mac Baren Cherry Choice 40 г.</h5>
				</div>
				<div className={classes.cardBuy}>
					<h4>180 грн</h4>
					<CustomButton
						primary={colors.primary}
						secondary={colors.secondary}
						text="КУПИТИ"
						action={(e) => console.log(e)}
						styles={buttonStyles}
						enabled={false}
					/>
				</div>
			</div>
		</div>
	);
}
