		{
			img: cigarette1,
			type: "Тютюн для самокруток",
			name: "Mac Baren Cherry Choice 40 г.",
			price: "180 грн",
		},
		{
			img: noCigarette,
			type: "Тютюн для самокруток",
			name: "Mac Baren Cherry Choice 40 г.",
			price: "180 грн",
		},
		{
			img: cigarette1,
			type: "Тютюн для самокруток",
			name: "Mac Baren Cherry Choice 40 г.",
			price: "180 грн",
		},
	],
	total: 0,
	filters: null,
	currentPage: 1,
};

export const tobaccoReducer = (state = initialState, action) => {
	switch (action.type) {
		case FETCH_TOBACCO:
			return { ...state, cards: action.payload };

		default:
			return state;
	}
};
