export const sorting = (items, filters) => {
	let sorted = [...items];

	//////////////////////////////////

	let filterList;
	let newList = [];

	filters[0].filters.map((filtr) => {
		switch (filtr) {
			case "Бренд":
				filterList = items.map((item) => {
					return item.name;
				});
				filterList.sort();
				sorted.map((sortedItem, index) => {
					let oldIndex = index;
					filterList.map((filteredItem, index) => {
						if (sortedItem.name === filteredItem) {
							newList[index] = sorted[oldIndex];
						}
					});
				});
			default:
				console.log(items);
				newList = [...items];
		}
	});

	return newList;
	console.log(newList);

	//////////////////////////////////

	// sorted.sort((a, b) => b.name - a.name);

	// console.log(["You", "Me"].sort());
	// console.log(sorted);
	return sorted;
};
