import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useDispatch, useSelector } from "react-redux";
import CardInCart from "../components/itemCards/CardInCart";
import { colors } from "../theme/colors";
import CustomSelect from "../components/menuItems/CustomSelect";
import ControlInput from "../components/menuItems/ControlInput";
import { useForm, Controller } from "react-hook-form";
import Divider from "../components/Divider";

const useStyles = makeStyles({
	root: {
		"& h1": {
			padding: 40,
			paddingTop: 20,
			width: "100%",
			fontFamily: "Open Sans Condensed",
			textAlign: "center",
			fontSize: 54,
		},
	},
	form: {
		// display: "flex",
		"& h3": {
			fontSize: 34,
			fontWeight: 600,
			padding: "40px 0",
		},
	},
	mainOrder: {
		width: "fit-content",
		border: `1px solid ${colors.secondary}`,
		margin: 20,
		height: "fit-content",
		"& h2": {
			fontSize: 35,
			fontWeight: 600,
			padding: 30,
			textAlign: "center",
		},
	},
	cartItems: {
		margin: "0 20px 20px 20px",
		maxHeight: 620,
		overflow: "scroll",
		"&::-webkit-scrollbar": {
			width: 5,
			height: 0,
			borderRadius: "10px",
		},
		"&::-webkit-scrollbar-thumb": {
			background: colors.blackGrey,
			borderRadius: "10px",
		},
	},
	notFound: {
		fontSize: 30,
		textAlign: "center",
		margin: "30px 0",
		width: "100%",
	},
	wholeForm: {
		display: "flex",
	},
	mainForm: {
		width: "35%",
		marginLeft: "7%",
	},
	userInfo: {
		"& h3": {
			paddingBottom: "0px !important",
		},
	},
	bottomForm: {
		width: "100%",
		maxWidth: 600,
		marginLeft: "20%",
		"& textarea": {
			width: "95%",
			display: "block",
			border: `1px solid ${colors.secondary}`,
			resize: "none",
			fontWeight: 300,
			fontSize: 20,
			padding: 5,
		},
	},
	formTotal: {
		display: "flex",
		"& h2": {
			fontWeight: 600,
			fontSize: 36,
			"&:last-child": {
				marginLeft: "10%",
			},
		},
	},
	formButton: {
		fontFamily: "Open Sans Condensed",
		padding: 5,
		width: "100%",
		backgroundColor: colors.secondary,
		color: colors.primary,
		fontWeight: 700,
		fontSize: 36,
		margin: "50px 0",
	},
});

export default function Order() {
	const classes = useStyles();

	const cart = useSelector((state) => state.cart.cart);
	const total = useSelector((state) => state.cart.total);
	const paymenntChilds = ["Карта приватбанк", "Наложений платіж"];
	const deliveryChilds = ["Нова пошта", "Самовивіз"];

	const { handleSubmit, control, setValue, register } = useForm();

	const onSubmit = (data) => {
		console.log(data);
	};

	const deliveryInputs = [
		{
			name: "city",
			text: "Місто",
		},
		{
			name: "department",
			text: "Відділення",
		},
	];

	const informationInputs = [
		{
			name: "name",
			text: "Ім'я",
		},
		{
			name: "surname",
			text: "Прізвище",
		},
		{
			name: "phone",
			text: "Номер телефону",
		},
		{
			name: "email",
			text: "e-mail",
		},
	];

	const inputStyle = {
		border: `1px solid ${colors.secondary}`,
		maxWidth: 400,
		fontSize: 20,
		marginTop: 40,
	};

	return (
		<div className={classes.root}>
			<h1>Оформлення замовлення</h1>
			<form className={classes.form} onSubmit={handleSubmit(onSubmit)}>
				<div className={classes.wholeForm}>
					<div className={classes.mainForm}>
						<div>
							<h3>Спосіб оплати</h3>
							<CustomSelect
								text="Оберіть спосіб оплати"
								childs={paymenntChilds}
								name="payment"
								control={control}
								Controller={Controller}
								setValue={setValue}
							/>
						</div>
						<div>
							<h3>Спосіб доставки</h3>
							<CustomSelect
								text="Оберіть спосіб доставки"
								childs={deliveryChilds}
								name="delivery"
								control={control}
								Controller={Controller}
								setValue={setValue}
							/>

							{deliveryInputs.map((deliveryItem, index) => (
								<ControlInput
									key={index}
									name={deliveryItem.name}
									control={control}
									text={deliveryItem.text}
									Controller={Controller}
									styles={inputStyle}
								/>
							))}
						</div>
						<div className={classes.userInfo}>
							<h3>Інформація про одержувача</h3>
							{informationInputs.map((informationInput, index) => (
								<ControlInput
									key={index}
									name={informationInput.name}
									control={control}
									text={informationInput.text}
									Controller={Controller}
									styles={inputStyle}
								/>
							))}
						</div>
					</div>

					<div className={classes.mainOrder}>
						<h2>Ваше замовлення</h2>
						<div className={classes.cartItems}>
							{cart[0] ? (
								cart.map((cartItem, index) => {
									return (
										<CardInCart itemCard={cartItem} key={index} index={index} />
									);
								})
							) : (
								<div className={classes.notFound}>Виберіть товар</div>
							)}
						</div>
					</div>
				</div>

				<div className={classes.bottomForm}>
					<h3>Коментар</h3>
					<Controller
						name="comment"
						control={control}
						defaultValue=""
						render={({ field }) => (
							<textarea cols="30" rows="6" {...field}></textarea>
						)}
					/>
					<Divider styles={{ margin: "35px 0 20px 0" }} />
					<div className={classes.formTotal}>
						<h2>До оплати:</h2>
						<h2>{total} грн</h2>
					</div>
					<button className={classes.formButton} type="submit">
						Підтвердити замовлення
					</button>
				</div>
			</form>
		</div>
	);
}
