import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import MainCard from "./MainCard";
import { useSelector } from "react-redux";
import longArrow from "../../theme/images/longArrow.svg";

const cardSize = 300;
const cardMargin = 50;
const arowLength = 100;
const cardLength = cardSize + cardMargin;
const menuSize = 220;

const useStyles = makeStyles((carouselWidth, translateX) => ({
	root: {
		height: "fit-content",
		alignItems: "center",
		width: (carouselWidth) => carouselWidth + arowLength,
		transition: "width .3s",
		position: "relative",
		display: "flex",
	},
	carousel: {
		width: (carouselWidth) => carouselWidth,
		transition: "width .3s",
		overflow: "hidden",
		height: "100%",
	},
	carouselInner: {
		display: "flex",
		width: "fit-content",
		transform: (f) => `translateX(${f}px)`,
	},
	longArrow: {
		position: "absolute",
		backgroundColor: "black",
		padding: "19px 15px",
		right: 10,
		borderRadius: "50%",
		cursor: "pointer",
	},
}));

export default function Carousel() {
	const menuWidth = useSelector((state) => state.app.menuWidth);

	const [translateX, setTranslateX] = useState(100);

	const setWidth = (callFromMenu) => {
		// contentWidth - with delay from menuWidth call
		let menu = 0;
		if (callFromMenu) {
			menu = !menuWidth ? menuSize : -menuSize;
		}
		let contentWidth = content.clientWidth - arowLength + menu;
		let counter = 0;

		while (true) {
			contentWidth -= cardLength;
			if (contentWidth > 0) {
				counter++;
			} else {
				break;
			}
		}
		setCarouselWidth(cardLength * counter);
	};

	const [carouselWidth, setCarouselWidth] = useState(0);

	useEffect(() => {
		let callFromMenu = true;
		setWidth(callFromMenu);
	}, [menuWidth]);

	useEffect(() => {
		const content = document.querySelector("#content");
		setWidth();
	}, []);

	const classes = useStyles(carouselWidth, translateX);

	window.addEventListener("resize", () => setWidth());

	const tobaccos = useSelector((state) => state.tobacco.cards);

	return (
		<div className={classes.root}>
			<div id="carousel" className={classes.carousel}>
				<div className={classes.carouselInner}>
					{tobaccos.map((tobacco, index) => {
						return <MainCard key={index} tobacco={tobacco} />;
					})}
				</div>
			</div>
			<img
				className={classes.longArrow}
				src={longArrow}
				alt="right-arrow"
				// onClick={}
			/>
		</div>
	);
}
