import {
	FETCH_CARDS,
	MENU_WIDTH,
	PUSH_ITEM_CART,
	DEL_ITEM_CART,
	COUNT_TOTAL,
	CHANGE_AMOUNT,
} from "./types";

// export const fetchItems = (itemsType) => {
// 	return async (dispatch) => {
// 		dispatch({ type: FETCH_CARDS, payload: {} });
// 	};
// };

export const changeWidthMenu = (menuWidth) => {
	return (dispatch) => {
		dispatch({ type: MENU_WIDTH, payload: menuWidth });
	};
};

export const addToCart = (itemCard, cart) => {
	return async (dispatch) => {
		let counter = 0;
		if (cart) {
			let isInCart = false;
			cart.map((cartItem, index) => {
				if (cartItem.id === itemCard.id) {
					const newCart = cart;
					itemCard.available && (newCart[index].amount += 1);
					dispatch({ type: CHANGE_AMOUNT, payload: newCart });
					dispatch({ type: COUNT_TOTAL });
					isInCart = true;
					return;
				}
			});
			if (!isInCart) {
				itemCard.available && (itemCard.amount += 1);
				dispatch({ type: PUSH_ITEM_CART, payload: itemCard });
				dispatch({ type: COUNT_TOTAL, payload: counter });
			}
		} else {
			itemCard.available && (itemCard.amount += 1);
			dispatch({ type: PUSH_ITEM_CART, payload: itemCard });
			dispatch({ type: COUNT_TOTAL });
		}
	};
};

export const changeAmount = (inputValue, cart, index) => {
	return async (dispatch) => {
		let counter = 0;
		const newCart = cart;
		inputValue === "" ? (inputValue = 0) : (inputValue = +inputValue);
		newCart[index].amount = inputValue;
		dispatch({ type: CHANGE_AMOUNT, payload: newCart });
		dispatch({ type: COUNT_TOTAL });
	};
};

export const delFromCart = (itemCard, cart) => {
	return async (dispatch) => {};
};
