import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useDispatch, useSelector } from "react-redux";
import CustomButton from "../components/menuItems/CustomButton";
import ItemSlider from "../components/itemCards/ItemSlider";
import { colors } from "../theme/colors";
import { addToCart } from "../redux/actions";

const useStyles = makeStyles({
	root: {
		display: "flex",
		// "& h1": {
		// 	width: "fit-content",
		// 	marginLeft: 30,
		// 	marginTop: 50,
		// 	fontFamily: "Open Sans Condensed",
		// 	fontSize: 54,
		// },
	},
	info: {
		flexGrow: 1,
	},
	itemInfo: {
		padding: 30,
		"& h3": {
			padding: " 0 0 30px 0",
			fontSize: 25,
			lineHeight: "33.25px",
			fontWeight: 600,
		},
	},
	itemId: {
		fontWeight: 300,
		fontSize: 17,
		width: "100%",
		maxWidth: 340,
		display: "flex",
		justifyContent: "space-between",
		"& p": {
			color: "#7D7E7D",
		},
	},
	itemPrice: {
		display: "flex",
		alignItems: "center",
		"& h2": {
			fontSize: 30,
			fontWeight: 600,
			marginTop: 15,
		},
	},
});

export default function Article() {
	const dispatch = useDispatch();
	const classes = useStyles();
	let splitedHref = window.location.href.split("/");
	let currentItem = splitedHref[splitedHref.length - 1];

	const item = useSelector((state) => state.cards.cards.tobacco[1]);
	const cart = useSelector((state) => state.cart.cart);

	const [characteristics, setCharacteristics] = useState([
		{
			title: "Призначення тютюну",
			value: "Для самокруток",
		},
		{
			title: "Бренд",
			value: "Mac Baren",
		},
		{
			title: "Міцність",
			value: "Середньо-м'який",
		},
		{
			title: "Аромат",
			value: "Кава",
		},
		{
			title: "Склад",
			value: "Virginia, Burley, Oriental",
		},
		{
			title: "Країна виробництва",
			value: "Данія",
		},
	]);

	let buttonStyles = {
		fontWeight: 500,
		fontSize: 28,
		borderRadius: 3,
		padding: "10px 30px",
		width: "fit-content",
	};

	return (
		<div className={classes.root}>
			<ItemSlider item={item} />
			<div className={classes.info}>
				<div className={classes.itemInfo}>
					<h3>
						{item.type} <br /> {item.name}
					</h3>
					<div className={classes.itemId}>
						{item.available ? (
							<span style={{ color: "green" }}>В наявності</span>
						) : (
							<span style={{ color: "red" }}>Немає в наявності</span>
						)}
						<p>Артикул: {item.id}</p>
					</div>
					<div className={classes.itemPrice}>
						<div>
							<h2>{item.price} грн/шт</h2>
							<h2>{item.price * 10} грн/блок</h2>
						</div>
						<div>
							<CustomButton
								primary={colors.secondary}
								secondary={colors.primary}
								text="КУПИТИ"
								action={() => dispatch(addToCart(item, cart))}
								styles={buttonStyles}
							/>
						</div>
					</div>
				</div>
				<div></div>
				<div></div>
			</div>
		</div>
	);
}
