import { STATE_MENU } from "../types";

const initialState = {
	menu: true,
};

export const appReducer = (state = initialState, action) => {
	switch (action.type) {
		case STATE_MENU:
			return { ...state, menu: action.payload };

		default:
			return state;
	}
};
