import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";
import cartIcon from "../../theme/images/cart.svg";
import { ClickAwayListener } from "@material-ui/core";
// import { addToCart } from "../../redux/actions";
import { useDispatch, useSelector } from "react-redux";
import CardInCart from "../itemCards/CardInCart";
import CustomButton from "../menuItems/CustomButton";
import { colors } from "../../theme/colors";

// zIndex all in Modal 2000 or take off conteiner from modal

const useStyles = makeStyles((theme) => ({
  modal: {
    display: "flex",
    position: "absolute !important",
    justifyContent: "center",
  },
  paperContainer: {
    display: "flex",
    width: "100%",
    maxWidth: 2000,
    alignItems: "flex-start",
    justifyContent: "flex-end",
  },
  paper: {
    margin: "120px 80px 0 0",
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    display: "flex",
    flexDirection: "column",
    height: "fit-content",
    "@media (max-width: 1300px)": {
      marginTop: 160,
    },
    "& h1": {
      width: "fit-content",
      marginLeft: 50,
      fontFamily: "Open Sans Condensed",
      fontSize: 54,
      marginBottom: 30,
    },
  },
  cartButton: {
    display: "flex",
    alignItems: "center",
    backgroundColor: "initial",
    "& p": {
      paddingLeft: 10,
      fontSize: 20,
      fontFamily: "Open Sans Condensed",
      fontWeight: 700,
    },
  },
  overModal: {
    position: "absolute",
    display: "flex",
    width: "100%",
    height: "100%",
    justifyContent: "center",
    left: 0,
  },
}));

export default function Cart() {
  const dispatch = useDispatch();
  const classes = useStyles();
  const [open, setOpen] = useState(false);

  const handleOpen = () => {
    setOpen(!open);
  };

  const cart = useSelector((state) => state.cart.cart);

  let buttonStyles = {
    fontWeight: 600,
    fontSize: 20,
    minWidth: 80,
    borderRadius: 3,
    backgroundColor: colors.semiWhite,
    padding: "5px 12px",
    marginRight: 15,
  };

  return (
    <div>
      <button className={classes.cartButton} type="button" onClick={handleOpen}>
        <img src={cartIcon} alt="cart" />
        <p>Корзина</p>
      </button>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleOpen}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
          style: {
            backgroundColor: "transparent",
          },
        }}
      >
        <div className={classes.paperContainer}>
          <Fade in={open}>
            <ClickAwayListener onClickAway={handleOpen}>
              <div className={classes.paper}>
                <h1>Корзина</h1>
                <div className={classes.cartItems}>
                  {cart &&
                    cart.map((cartItem, index) => {
                      return <CardInCart itemCard={cartItem} key={index} />;
                    })}
                </div>
                <div className={classes.cartTotal}>
                  <CustomButton
                    primary={colors.secondary}
                    secondary={colors.primary}
                    text="Перейти до оформлення замовлення"
                    action={() => console.log(cart)}
                    styles={buttonStyles}
                    enabled={false}
                  />
                </div>
              </div>
            </ClickAwayListener>
          </Fade>
        </div>
      </Modal>
    </div>
  );
}
