import { FETCH_TOBACCO } from "../types";

const initialState = {
	cards: [],
	total: 0,
	filters: null,
	currentPage: 1,
};

export const tobaccoReducer = (state = initialState, action) => {
	switch (action.type) {
		case FETCH_TOBACCO:
			return { ...state, cards: action.payload };

		// case FETCH_ARTICLES:
		// 	return {...state, cards: action.payload}

		// case SET_CURRENT_PAGE:
		// 	return {...state, currentPage: action.payload}

		// case SET_FILTERS:
		// 	return {...state, filters: action.payload}
		default:
			return state;
	}
};
