import React, { useState, useEffect, useRef } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import MainCard from "./MainCard";
import { useSelector } from "react-redux";

const cardSize = 300;
const cardMargin = 50;

const useStyles = makeStyles({
	carousel: {
		flexGrow: 1,
		overflow: "hidden",
	},
	carouselInner: {
		display: "flex",
		width: "fit-content",
	},
});

export default function Carousel() {
	const classes = useStyles();
	const carousel = useRef(null);

	useEffect(() => {
		carousel.current.addEventListener("resize", () =>
			console.log(document.querySelector("#carousel").clientWidth)
		);
	}, []);

	const tobaccos = useSelector((state) => state.tobacco.cards);

	return (
		<div id="carousel" ref={carousel} className={classes.carousel}>
			<div className={classes.carouselInner}>
				{tobaccos.map((tobacco, index) => {
					return <MainCard key={index} tobacco={tobacco} />;
				})}
			</div>
		</div>
	);
}
