import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import bottomArrow from "../../theme/images/bottom-arrow.svg";
import { colors } from "../../theme/colors";

const useStyles = makeStyles({
	select: {
		display: "flex",
		justifyContent: "space-between",
		width: "100%",
		maxWidth: 300,
		padding: 5,
		color: colors.secondary,
		border: `1px soled ${colors.secondsry}`,
	},
});

export default function CustomSelect({ text, childs }) {
	const classes = useStyles();
	const [selectText, setSelectText] = useState(text);

	const changeSelect = (selectedValue) => {
		setSelectText(selectedValue);
	};

	return (
		<div>
			<div className={classes.select}>
				<span>{selectText}</span>
				<img src={bottomArrow} alt="select" />
			</div>
			<div className={classes.selectChilds}>
				{childs.map((child, index) => {
					return (
						<span key={index} onClick={(e) => changeSelect(e.target.value)}>
							{child}
						</span>
					);
				})}
			</div>
		</div>
	);
}
