import {
	FETCH_CARDS,
	MENU_WIDTH,
	PUSH_ITEM_CART,
	DEL_ITEM_CART,
	COUNT_TOTAL,
	CHANGE_AMOUNT,
} from "./types";

// export const fetchItems = (itemsType) => {
// 	return async (dispatch) => {
// 		dispatch({ type: FETCH_CARDS, payload: {} });
// 	};
// };

export const changeWidthMenu = (menuWidth) => {
	return (dispatch) => {
		dispatch({ type: MENU_WIDTH, payload: menuWidth });
	};
};

export const addToCart = (itemCard, cart) => {
	return async (dispatch) => {
		cart.map((cartItem) => {
			if (itemCard.id === cartItem.id && cartItem.amount) {
				dispatch({ type: CHANGE_AMOUNT, payload: itemCard });
				dispatch({ type: COUNT_TOTAL, payload: itemCard });
				return;
			}
			dispatch({ type: PUSH_ITEM_CART, payload: itemCard });
			dispatch({ type: COUNT_TOTAL, payload: itemCard });
		});
	};
};
