import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import ControlInput from "../menuItems/ControlInput";
import Nav from "./components/Nav";
import Cart from "../cart/Cart";
import searchIcon from "../../theme/images/search.svg";
import { colors } from "../../theme/colors";
import CustomButton from "../menuItems/CustomButton";
import { useDispatch, useSelector } from "react-redux";
import { changeWidthMenu } from "../../redux/actions";
import { useForm, Controller } from "react-hook-form";
import ClickAwayListener from "@material-ui/core/ClickAwayListener";

const headerHeight = 60;

// Breakpoints: 1300, 780

const useStyles = makeStyles({
	header: {
		display: "flex",
		justifyContent: "center",
		width: "100%",
		borderTop: `1px solid ${colors.secondary}`,
		borderBottom: `1px solid ${colors.secondary}`,
	},
	container: {
		display: "flex",
		flexDirection: "row",
		width: "100%",
		maxWidth: 2000,
		"@media (max-width: 1300px)": {
			flexDirection: "column",
		},
	},
	headerPart: {
		display: "flex",
		"&:last-child": {
			width: "100%",
			justifyContent: "space-between",
			"@media (max-width: 1300px)": {
				borderTop: `1px solid ${colors.secondary}`,
			},
		},
	},
	search: {
		flexGrow: 1,
		padding: "0 10px 0 25px",
		display: "flex",
		alignItems: "center",
		borderRight: `1px solid ${colors.secondary}`,
		"& span": {
			display: "flex",
		},
		"& button": {
			backgroundColor: "inherit",
		},
		"& img": {
			cursor: "pointer",
			height: 35,
		},
	},
	cart: {
		padding: "5px 42px 5px 20px",
	},
});

export default function Header() {
	const classes = useStyles();
	const dispatch = useDispatch();
	const menuWidth = useSelector((state) => state.app.menuWidth);
	const currentPath = useSelector((state) => state.app.location);

	const [searchFocused, setSearchFocused] = useState(false);

	const openMenu = () => {
		dispatch(changeWidthMenu(220));
	};
	const closeMenu = () => {
		dispatch(changeWidthMenu(0));
	};

	const { handleSubmit, control } = useForm();

	const buttonStyles = {
		fontWeight: 700,
		fontSize: 25,
		minWidth: 220,
		height: headerHeight,
		fontFamily: "Open Sans Condensed",
		"@media (max-width: 870px)": {
			position: "fixed",
		},
	};

	const inputStyle = {
		fontSize: 25,
		maxWidth: 180,
	};

	const searchRequest = (data) => {
		console.log(data);
	};

	return currentPath === "/order" ? null : (
		<header className={classes.header}>
			<div className={classes.container}>
				<div className={classes.headerPart}>
					<CustomButton
						primary={colors.secondary}
						secondary={colors.primary}
						text="Каталог"
						action={menuWidth ? closeMenu : openMenu}
						styles={buttonStyles}
						enabled={false}
					/>
					<Nav headerHeight={headerHeight} />
				</div>

				<div className={classes.headerPart}>
					<form
						onSubmit={handleSubmit(searchRequest)}
						className={classes.search}
					>
						<ClickAwayListener onClickAway={() => setSearchFocused(false)}>
							<span onClick={() => setSearchFocused(true)}>
								<ControlInput
									styles={inputStyle}
									Controller={Controller}
									name="search"
									control={control}
									searchFocused={searchFocused}
									text="Пошук товарів"
								/>
								<button type="submit">
									<img type="submit" src={searchIcon} alt="search" />
								</button>
							</span>
						</ClickAwayListener>
					</form>
					<div className={classes.cart}>
						<Cart />
					</div>
				</div>
			</div>
		</header>
	);
}
