import {
	FETCH_CARDS,
	MENU_WIDTH,
	PUSH_ITEM_CART,
	DEL_ITEM_CART,
	COUNT_TOTAL,
} from "./types";
import { useSelector } from "react-redux";

const cart = useSelector((state) => state.cart.cart);

// export const fetchItems = (itemsType) => {
// 	return async (dispatch) => {
// 		dispatch({ type: FETCH_CARDS, payload: {} });
// 	};
// };

export const changeWidthMenu = (menuWidth) => {
	return (dispatch) => {
		dispatch({ type: MENU_WIDTH, payload: menuWidth });
	};
};

export const addToCart = (itemCard) => {
	return async (dispatch) => {
		// cart.map(cartItem => {
		// 	cartItem.amount ?
		// })
		// itemCard.amount = 1;
		dispatch({ type: PUSH_ITEM_CART, payload: itemCard });
		dispatch({ type: COUNT_TOTAL, payload: itemCard.price });
	};
};
