import {
	ADD_FILTER,
	REMOVE_FILTER,
	FETCH_PAPER,
	FETCH_HILZY,
	FETCH_FILTERS,
	FETCH_AROMA,
	FETCH_MASHINE,
} from "../types";
import cigarette1 from "../../theme/images/cigarette1.png";
import noCigarette from "../../theme/images/no-cigarette.png";

const initialState = {
	cards: {
		tobacco: [],
		consumables: [],
		cigarettes: [],
		accessories: [],
	},
	total: 0,
	filters: [],
	currentPage: 1,
};

export const cardReducer = (state = initialState, action) => {
	switch (action.type) {
		case FETCH_CARDS:
			return { ...state, cards: action.payload };

		// case FETCH_ARTICLES:
		// 	return {...state, cards: action.payload}

		// case SET_CURRENT_PAGE:
		// 	return {...state, currentPage: action.payload}

		case ADD_FILTER:
			return { ...state, filters: state.filters.concat(action.payload) };

		case REMOVE_FILTER:
			let copyFilters = [...state.filters];
			copyFilters.splice(action.payload, 1);
			return { ...state, filters: copyFilters };

		default:
			return state;
	}
};
