import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Catalog from "../menu/catalog"
import CustomInput from "../menuItems/CustomInput"
import Nav from "./components/Nav"
import searchIcon from "../../theme/images/search.svg"
import { colors } from '../../theme/colors'

const headerHeight = 60;

const useStyles = makeStyles({
	header: {
		display: "flex",
	},
	search: {
		width: "100%",
		padding: "0 10px 0 25px",
		display: "flex",
		alignItems: "center",
		position: "relative",
		border: `1px solid ${colors.secondary}`,
		borderLeft: "none",
		"& img": {
			cursor: "pointer",
			height: 35,
		}
	},
	focus: {
		width: 240,
		transition: "width .3s"
	},
})

export default function Header() {
  const classes = useStyles();
  const [focused, setFocused] = useState(false)

  const inputStyle = {

  }

  const cahngeWidth = () => {
  	setFocused(true)
  }

  return (
    <header className={classes.header}>
      <Catalog headerHeight={headerHeight}/>
      <Nav headerHeight={headerHeight}/>

	      <span className={classes.search}>
		      <CustomInput
		      	action={cahngeWidth}
						text="Пошук товарів"
						styles={inputStyle}
		      />
		      <img onClick={cahngeWidth} src={searchIcon} alt="search"/>
	      </span>

    </header>
  );
}
