import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import { useHistory } from "react-router-dom";

const useStyles = makeStyles({
	coments: {
		padding: 50,
	},
	comentsHeader: {
		paddingBottom: 50,
		display: "flex",
		justifyContent: "space-between",
		alignItems: "center",
		"& h1": {
			fontSize: 35,
			fontWeight: 600,
		},
		"& p": {
			fontSize: 25,
			cursor: "pointer",
		},
	},
});

export default function Coments() {
	const classes = useStyles();
	const history = useHistory();

	const coments = [
		{
			name: "Ольга",
			text: `Тютюн тонкої нарізки можна використовувати і для скручування самокруток і для забивання гільз. Суміш, що складається з солодкуватої Вірджинії, гіркуватого Берлі та пряного Орієнтал, приправлена ​​натуральним кавовим ароматом.`,
			time: "DD. MM.YYYY",
		},
	];

	return (
		<div className={classes.coments}>
			<div className={classes.comentsHeader}>
				<h1>Відгуки</h1>
				<p onClick={() => history.push("/feedbacks")}>
					Бажаєте залишити відгук?
				</p>
			</div>
		</div>
	);
}
