import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import { colors } from "../../theme/colors";
import CustomButton from "../menuItems/CustomButton";

const drawerWidth = 220;

const useStyles = makeStyles((headerHeight) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    position: "relative",
    // height: "100%",
    flexGrow: 1,
    maxWidth: drawerWidth,
  },
  hide: {
    display: "none",
  },
  drawer: {
    width: drawerWidth,
  },
  drawerPaper: {
    width: drawerWidth,
    borderRight: `1px solid ${colors.secondary}`,
    // height: "calc(100% - 94px)",
    height: "100%",
    position: "absolute",
    // marginTop: (headerHeight) => 34 + headerHeight,
    marginTop: (headerHeight) => headerHeight,
  },
}));

export default function Catalog({ headerHeight }) {
  const classes = useStyles(headerHeight);
  const [open, setOpen] = useState(true);

  const handleDrawer = () => {
    setOpen(!open);
  };

  let buttonStyles = {
    fontWeight: 700,
    fontSize: 25,
    width: drawerWidth,
    height: headerHeight,
    fontFamily: "Open Sans Condensed",
  };

  return (
    <div className={classes.root}>
      <CustomButton
        primary={colors.secondary}
        secondary={colors.primary}
        text="Каталог"
        action={handleDrawer}
        styles={buttonStyles}
        enabled={false}
      />
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={open}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <List>
          {["Табак", "Витратники", "Сигари", "Аксесуари"].map((text, index) => (
            <ListItem button key={text}>
              <ListItemText primary={text} />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </div>
  );
}
