import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Catalog from "../menu/catalog"
import CustomInput from "../menuItems/CustomInput"
import Nav from "./components/Nav"
import searchIcon from "../../theme/images/search.svg"

const headerHeight = 60;

const useStyles = makeStyles({
	header: {
		display: "flex",
	},
	search: {
		display: "flex",
		aignItems: "center",
		position: "relative",
	},
	find: {
		display: "flex",
		aignItems: "center",
		position: "absolute",
		cursor: "pointer",
		height: "100%",
		"& img": {
			marginLeft: 200,
		}
	}
})

export default function Header() {
  const classes = useStyles();

  return (
    <header className={classes.header}>
      <Catalog headerHeight={headerHeight}/>
      <Nav headerHeight={headerHeight}/>
      <span className={classes.search}>
	      <CustomInput
					text="Пошук товарів"
					styles={{width: 300}}
	      />
	      <span className={classes.find}><img src={searchIcon} alt="search"/></span>
      </span>
    </header>
  );
}
