import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import { colors } from "../../theme/colors";
import { useSelector } from "react-redux";
import CatalogFilter from "./components/CatalogFilter";

const useStyles = makeStyles((menuWidth) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    position: "relative",
    flexGrow: 1,
    maxWidth: (menuWidth) => menuWidth,
    transition: ".2s",
  },
  hide: {
    display: "none",
  },
  drawer: {
    width: (menuWidth) => menuWidth,
    transition: ".2s",
  },
  drawerPaper: {
    height: "fit-content",
    padding: 13,
    width: (menuWidth) => menuWidth,
    transition: ".2s",
    borderRight: `1px solid ${colors.secondary}`,
    position: "absolute",
  },
  filterClass: {
    fontSize: 18,
  },
}));

export default function Catalog() {
  const menuWidth = useSelector((state) => state.app.menuWidth);
  const currentPath = useSelector((state) => state.app.location);
  const classes = useStyles(menuWidth);

  const fil = {
    name: "Табак",
    subMenu: [
      {
        subName: "Призначення табака",
        subList: [
          "Для самокруток",
          "Бля кальяну",
          "Для люльки",
          "Нюхальний табак",
        ],
      },
      {
        subName: "Бренд",
        subList: [
          "Бренд 1",
          "Бренд 2",
          "Бренд 4",
          "Бренд 5",
          "Бренд 6",
          "Бренд 7",
        ],
      },
      {
        subName: "Міцність",
        subList: [
          "М’який",
          "Середньо-м’який",
          "Середній",
          "Середньо-міцний",
          "Міцний",
        ],
      },
      {
        subName: "Країна виробництва",
        subList: ["Країна 1", "Країна 2", "Країна 3", "Країна 4", "Країна 5"],
      },
    ],
  };
  const fl = { ...fil };
  fl.name = "Витратники";
  const f = { ...fil };
  f.name = "Сигари";
  const f1 = { ...fil };
  f1.name = "Аксесуари";

  const menuItems = [fil, fl, f, f1];

  return currentPath === "/order" ? null : (
    <div className={classes.root}>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={Boolean(menuWidth)}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <List>
          {menuItems.map((menuItem, index) => (
            <div key={index} className={classes.filterClass}>
              <h2>{menuItem.name}</h2>
              {menuItem.subMenu.map((filterClasses, index) => (
                <div key={index} className={classes.filtersList}>
                  <h3>{filterClasses.subName}</h3>
                  {filterClasses.subList.map((filterItem, index) => (
                    <CatalogFilter key={index} filterName={filterItem} />
                  ))}
                </div>
              ))}
            </div>
          ))}
        </List>
      </Drawer>
    </div>
  );
}
