import { MENU_WIDTH } from "../types";

const initialState = {
	menuWidth: true,
};

export const appReducer = (state = initialState, action) => {
	switch (action.type) {
		case MENU_WIDTH:
			return { ...state, menu: action.payload };

		default:
			return state;
	}
};
