import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../theme/colors";
import ControlInput from "../components/menuItems/ControlInput";
import { useForm, Controller } from "react-hook-form";

const useStyles = makeStyles({
	root: {
		// "& h1": {
		// 	padding: 40,
		// 	paddingTop: 20,
		// 	width: "100%",
		// 	fontFamily: "Open Sans Condensed",
		// 	textAlign: "center",
		// 	fontSize: 54,
		// }
	},
});

export default function Feedbacks() {
	const classes = useStyles();

	const { handleSubmit, control, setValue } = useForm();

	const onSubmit = (data) => {
		const TIME = new Date(Date.now()).toLocaleString().split(",")[0].split("/");
		const reversedTime = [...TIME];
		ReversedTime[0] = TIME[1] < 10 ? "0" + TIME[1] : TIME[1];
		ReversedTime[1] = TIME[0] < 10 ? "0" + TIME[0] : TIME[0];

		data.timestamp = ReversedTime.join(".");
		console.log(data);
	};

	const inputStyle = {
		border: `1px solid ${colors.secondary}`,
		maxWidth: 400,
		fontSize: 20,
	};

	return (
		<div className={classes.root}>
			<h1>Бажаєте залишити відгук?</h1>
			<form className={classes.form} onSubmit={handleSubmit(onSubmit)}>
				<div className={classes.userInfo}>
					<h3>Інформація про одержувача</h3>
					{informationInputs.map((informationInput, index) => (
						<ControlInput
							key={index}
							name={informationInput.name}
							control={control}
							text={informationInput.text}
							Controller={Controller}
							styles={inputStyle}
						/>
					))}
				</div>

				<div className={classes.mainOrder}>
					<h2>Ваше замовлення</h2>
					<div className={classes.cartItems}>
						{cart[0] ? (
							cart.map((cartItem, index) => {
								return (
									<CardInCart itemCard={cartItem} key={index} index={index} />
								);
							})
						) : (
							<div className={classes.notFound}>Виберіть товар</div>
						)}
					</div>
				</div>
				<div className={classes.bottomForm}>
					<h3>Коментар</h3>
					<Controller
						name="comment"
						control={control}
						defaultValue=""
						render={({ field }) => (
							<textarea cols="30" rows="8" {...field}></textarea>
						)}
					/>
					<Divider styles={{ margin: "35px 0 20px 0" }} />
					<div className={classes.formTotal}>
						<h2>До оплати:</h2>
						<h2>{total} грн</h2>
					</div>
					<button className={classes.formButton} type="submit">
						Залишити відгук
					</button>
				</div>
			</form>
		</div>
	);
}
