import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../../theme/colors";
import { useDispatch } from "react-redux";
import clsx from "clsx";

const useStyles = makeStyles({
  root: {
    display: "flex",
    cursor: "pointer",
    // alignItems: "center",
    // flexDirection: "column",
    // position: "relative",
    // flexGrow: 1,
    // maxWidth: (menuWidth) => menuWidth,
    // transition: ".2s",
  },
  filter: {
    fontSize: 18,
    fontWeight: 300,
  },
  filterActive: {
    fontWeight: 400,
  },
  checkbox: {
    width: 20,
    height: 20,
    border: `1px solid ${colors.secondary}`,
    borderRadius: 5,
  },
  checkboxActive: {
    backgroundColor: colors.secondary,
  },
});

export default function CatalogFilter({ filterName }) {
  const classes = useStyles();
  const [selectedFilter, setSelectedFilter] = useState(false);

  const handleSelect = (name) => {
    setSelectedFilter(!selectedFilter);
    console.log(name);
  };

  return (
    <div onClick={() => handleSelect(filterName)} className={classes.root}>
      <span
        className={clsx(
          classes.checkbox,
          selectedFilter && classes.checkboxActive
        )}
      ></span>
      <h4
        className={clsx(classes.filter, selectedFilter && classes.filterActive)}
      >
        {filterName}
      </h4>
    </div>
  );
}
