import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useDispatch, useSelector } from "react-redux";
import CardInCart from "../components/itemCards/CardInCart";
import { colors } from "../theme/colors";

const useStyles = makeStyles({
	root: {
		"& h1": {
			padding: 40,
			paddingTop: 20,
			width: "100%",
			fontFamily: "Open Sans Condensed",
			textAlign: "center",
			fontSize: 54,
		},
	},
	mainOrder: {
		width: "fit-content",
		border: `1px solid ${colors.secondary}`,
		margin: 20,
		"& h2": {
			fontSize: 35,
			fontWeight: 600,
			padding: 30,
			textAlign: "center",
		},
	},
	cartItems: {
		margin: "0 20px 20px 20px",
		maxHeight: 620,
		overflow: "scroll",
		wodth: "fit-content",
		"&::-webkit-scrollbar": {
			width: 5,
			height: 0,
			borderRadius: "10px",
		},
		"&::-webkit-scrollbar-thumb": {
			background: colors.blackGrey,
			borderRadius: "10px",
		},
	},
	notFound: {
		fontSize: 30,
		textAlign: "center",
		margin: "30px 0",
		width: "100%",
	},
	mainForm: {
		width: "35%",
	},
});

export default function Order() {
	const classes = useStyles();

	const cart = useSelector((state) => state.cart.cart);

	return (
		<div className={classes.root}>
			<h1>Оформлення замовлення</h1>
			<div className={classes.main}>
				<div className={classes.mainForm}></div>
				<div className={classes.mainOrder}>
					<h2>Ваше замовлення</h2>
					<div className={classes.cartItems}>
						{cart[0] ? (
							cart.map((cartItem, index) => {
								return (
									<CardInCart itemCard={cartItem} key={index} index={index} />
								);
							})
						) : (
							<div className={classes.notFound}>Виберіть товар</div>
						)}
					</div>
				</div>
			</div>
		</div>
	);
}
