import React, { useState, useEffect, useRef } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import MainCard from "./MainCard";
import { useSelector } from "react-redux";

const cardSize = 300;
const cardMargin = 50;
const cardLength = cardSize + cardMargin;
const menuSize = 220;
const maxWidth = 2000;

const useStyles = makeStyles((carouselWidth) => ({
	carousel: {
		width: (carouselWidth) => carouselWidth,
		flexGrow: 1,
		overflow: "hidden",
		position: "relative",
		height: "100%",
	},
	carouselInner: {
		display: "flex",
		width: "fit-content",
		position: "absolute",
	},
}));

export default function Carousel() {
	const carousel = useRef(null);

	const setWidth = () => {
		allWidth = window.innerWidth;

		if (windowWidth - allWidth > 300 || allWidth - windowWidth > 300) {
			console.log(allWidth);
			console.log("windowWidth", windowWidth);
			setWindowWidth(allWidth);
			let carouselLength = windowWidth < maxWidth ? windowWidth : maxWidth;
			setCarouselWidth(
				Math.floor((carouselLength - menuSize) / cardLength) * cardLength
			);
			document.querySelector("#carousel").style.width = `${carouselWidth}+px`;
		}
	};

	const [windowWidth, setWindowWidth] = useState(window.innerWidth);
	const [carouselWidth, setCarouselWidth] = useState(setWidth());

	const classes = useStyles(carouselWidth);

	window.addEventListener("resize", () => setWidth());

	useEffect(() => {}, []);

	const tobaccos = useSelector((state) => state.tobacco.cards);

	return (
		<div id="carousel" ref={carousel} className={classes.carousel}>
			<div className={classes.carouselInner}>
				{tobaccos.map((tobacco, index) => {
					return <MainCard key={index} tobacco={tobacco} />;
				})}
			</div>
		</div>
	);
}
