export const sorting = (items, filters) => {
	let sorted = [...items];

	//////////////////////////////////

	console.log(filters);
	let filterList;

	filters[0].filters.map((filtr) => {
		switch (filtr) {
			case "Бренд":
				filterList = items.map((item) => {
					return item.name;
				});
		}
		console.log(filterList);
	});

	//////////////////////////////////

	// sorted.sort((a, b) => b.name - a.name);

	// console.log(["You", "Me"].sort());
	// console.log(sorted);
	return sorted;
};
