import { FETCH_TOBACCO, MENU_WIDTH } from "./types";

export function fetchTobacco(tobacco) {
	return async (dispatch) => {
		dispatch({ type: FETCH_TOBACCO, payload: tobacco });
	};
}

export function changeWidthMenu(menuWidth) {
	return (dispatch) => {
		dispatch({ type: MENU_WIDTH, payload: menuWidth });
	};
}
