export const sorting = (items, filters) => {
	let sorted = [...items];

	//////////////////////////////////

	let filterList;
	let newList = [];

	let sortedList = filters[0].filters.map((filtr) => {
		switch (filtr) {
			case "Бренд":
				filterList = items.map((item) => {
					return item.name;
				});
				filterList.sort();
				sorted.map((sortedItem, index) => {
					let oldIndex = index;
					filterList.map((filteredItem, index) => {
						if (sortedItem.name === filteredItem) {
							newList[index] = sorted[oldIndex];
						}
					});
				});
		}
		console.log(sorted);
		console.log(newList);
		return newList;
	});

	//////////////////////////////////

	// sorted.sort((a, b) => b.name - a.name);

	// console.log(["You", "Me"].sort());
	// console.log(sorted);
	return sorted;
};
