import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { colors } from "../../theme/colors";
import { NavLink } from "react-router-dom";

const useStyles = makeStyles({
	footer: {
		width: "100%",
		backgroundColor: colors.secondary,
	},
});

export default function Footer() {
	const classes = useStyles();

	const [catalog, setCatalog] = useState([
		{
			name: "Табак",
			link: "/tobacco",
		},
		{
			name: "Витратники",
			link: "/consumables",
		},
		{
			name: "Сигари",
			link: "/cigarettes",
		},
		{
			name: "Аксесуари",
			link: "/accessories",
		},
	]);

	const [forClients, setForClients] = useState([
		{
			name: "Про нас",
			link: "/about",
		},
		{
			name: "Угода користувача",
			link: "/agreement",
		},
		{
			name: "Обмін та повернення",
			link: "/exchange",
		},
		{
			name: "Доставка і оплата",
			link: "/delivery",
		},
		{
			name: "Відгуки про магазин",
			link: "/feedbacks",
		},
	]);

	return (
		<footer className={classes.footer}>
			<h2 className={classes.footerLeft}>
				Відвідування та перегляд сайту особам до 18 років заборонено! Куріння
				шкідиве для вашого здоров’я
			</h2>

			<div className={classes.footerRight}>
				<div className={classes.catalog}>
					<h3>Каталог</h3>
					{catalog.map((catalogItem, index) => {
						return (
							<NavLink to={catalogItem.link} key={index}>
								{catalogItem.name}
							</NavLink>
						);
					})}
				</div>
				<div className={classes.forClients}>
					<h3>Клієнтам</h3>
					{forClients.map((forClientsItem, index) => {
						return (
							<NavLink to={forClientsItem.link} key={index}>
								{forClientsItem.name}
							</NavLink>
						);
					})}
				</div>
				<div className={classes.contactInfo}>
					<h3>Контактна Інформація</h3>
					<div>+ 38 (000) 000 00 00</div>
					<div>+ 38 (000) 000 00 00</div>
					<div>shop@gmail.com</div>
				</div>
			</div>
		</footer>
	);
}
