const URL = "http://api.imperiaua.com.ua/graphql/";

const myHeaders = new Headers();
myHeaders.append("Content-Type", "application/json");
myHeaders.append(
	"Cookie",
	"csrftoken=5LfOKYXRjFaKOr3JO6iAuTsCqHVvgLHDTFaDJ0IQR4bVuklhDaezC4qnEYbLh8Pl"
);

export const get = async ({ query }) => {
	let graphql = JSON.stringify({
		query: query,
		variables: {},
	});
	let requestOptions = {
		method: "GET",
		headers: myHeaders,
		body: graphql,
		redirect: "follow",
	};

	try {
		const response = await fetch(
			"http://api.imperiaua.com.ua/graphql/",
			requestOptions
		);
		const text = response.text();
	} catch (e) {
		console.log("error", e);
	}
};
